import React, { useState, useEffect } from 'react';
import { fetchWeather } from '../api/weatherAPI'; // Assuming fetchWeather is defined in your API module
import { fetchForecast } from '../api/fetchForecast';
import { fetchAirQuality } from '../api/fetchAirQuality';
import { fetchAstronomy } from '../api/fetchAstronomy';
import { Link } from 'react-router-dom'; // For navigation

const HomePage = () => {
  const [location, setLocation] = useState('New York');
  const [weather, setWeather] = useState(null);
  const [hourly, setHourly] = useState(null);
  const [daily, setDaily] = useState(null);
  const [alerts, setAlerts] = useState(null);
  const [airQuality, setAirQuality] = useState(null);
  const [astronomy, setAstronomy] = useState(null);

  useEffect(() => {
    fetchWeather(location).then(data => setWeather(data));
    fetchForecast(location).then(data => {
      setHourly(data.forecast.forecastday[0].hour);
      setDaily(data.forecast.forecastday);
    });
    fetchAirQuality(location).then(data => setAirQuality(data));
    fetchAstronomy(location).then(data => setAstronomy(data));
  }, [location]);

  return (
    <div className="home-page">
      <h1>Weather App</h1>

      {/* Search Bar */}
      <div>
        <input 
          type="text" 
          placeholder="Enter location" 
          value={location} 
          onChange={e => setLocation(e.target.value)} 
        />
        <button>Search</button>
      </div>

      {/* Current Weather */}
      {weather && (
        <div className="current-weather">
          <h2>{weather.location.name}</h2>
          <p>Temperature: {weather.current.temp_c} °C</p>
          <p>Condition: {weather.current.condition.text}</p>
        </div>
      )}

      {/* Hourly Forecast */}
      {hourly && (
        <div className="hourly-forecast">
          <h3>Hourly Forecast</h3>
          {hourly.map((hour, index) => (
            <div key={index}>
              <p>{hour.time} - Temp: {hour.temp_c} °C</p>
            </div>
          ))}
        </div>
      )}

      {/* Link to Settings Page */}
      <div className="settings-link">
        <Link to="/settings">Go to Settings</Link>
      </div>
    </div>
  );
};

export default HomePage;
