import React, { useState, useEffect } from 'react';
import { fetchWeather } from '../api/weatherAPI';
import { fetchAirQuality } from '../api/airQualityAPI';  // New API for air quality
import { fetchAstronomy } from '../api/astronomyAPI';    // New API for astronomy
import { fetchForecast } from '../api/forecastAPI';      // New API for forecast data

const HomePage = () => {
  const [location, setLocation] = useState('New York');
  const [weather, setWeather] = useState(null);
  const [hourly, setHourly] = useState(null);
  const [daily, setDaily] = useState(null);
  const [alerts, setAlerts] = useState(null);
  const [airQuality, setAirQuality] = useState(null);
  const [astronomy, setAstronomy] = useState(null);
  const [forecastGraph, setForecastGraph] = useState(null);
  const [timeZone, setTimeZone] = useState(null);

  useEffect(() => {
    fetchWeather(location).then(data => setWeather(data));
    fetchForecast(location).then(data => setHourly(data.hourly), setDaily(data.daily));
    fetchAirQuality(location).then(data => setAirQuality(data));
    fetchAstronomy(location).then(data => setAstronomy(data));
  }, [location]);

  const handleSearch = () => {
    fetchWeather(location).then(data => setWeather(data));
    fetchForecast(location).then(data => {
      setHourly(data.hourly);
      setDaily(data.daily);
    });
    fetchAirQuality(location).then(data => setAirQuality(data));
    fetchAstronomy(location).then(data => setAstronomy(data));
  };

  return (
    <div className="container">
      <h1>Weather App</h1>

      {/* Search Bar */}
      <div>
        <input 
          type="text" 
          placeholder="Enter location" 
          value={location} 
          onChange={e => setLocation(e.target.value)} 
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      {/* Current Weather */}
      {weather && (
        <div className="current-weather">
          <h2>{weather.location.name}</h2>
          <p>Temperature: {weather.current.temp_c} °C</p>
          <p>Condition: {weather.current.condition.text}</p>
          <p>Wind Speed: {weather.current.wind_kph} km/h</p>
          <p>Humidity: {weather.current.humidity}%</p>
          <p>Pressure: {weather.current.pressure_mb} mb</p>
          <p>Visibility: {weather.current.vis_km} km</p>
        </div>
      )}

      {/* Hourly Forecast */}
      {hourly && (
        <div className="hourly-forecast">
          <h3>Hourly Forecast</h3>
          {hourly.map((hour, index) => (
            <div key={index}>
              <p>{hour.time} - Temp: {hour.temp_c} °C</p>
              <p>Condition: {hour.condition.text}</p>
              <p>Wind Speed: {hour.wind_kph} km/h</p>
            </div>
          ))}
        </div>
      )}

      {/* Daily Forecast */}
      {daily && (
        <div className="daily-forecast">
          <h3>Daily Forecast</h3>
          {daily.map((day, index) => (
            <div key={index}>
              <p>{day.date} - Min Temp: {day.mintemp_c} °C | Max Temp: {day.maxtemp_c} °C</p>
              <p>Condition: {day.condition.text}</p>
            </div>
          ))}
        </div>
      )}

      {/* Weather Alerts */}
      {alerts && (
        <div className="alerts">
          <h3>Weather Alerts</h3>
          {alerts.map((alert, index) => (
            <div key={index}>
              <p>{alert.headline}</p>
              <p>{alert.description}</p>
            </div>
          ))}
        </div>
      )}

      {/* Air Quality */}
      {airQuality && (
        <div className="air-quality">
          <h3>Air Quality</h3>
          <p>PM2.5: {airQuality.pm25}</p>
          <p>PM10: {airQuality.pm10}</p>
          <p>NO2: {airQuality.no2}</p>
          <p>AQI: {airQuality.aqi}</p>
        </div>
      )}

      {/* Astronomy */}
      {astronomy && (
        <div className="astronomy">
          <h3>Astronomy Data</h3>
          <p>Sunrise: {astronomy.sunrise}</p>
          <p>Sunset: {astronomy.sunset}</p>
          <p>Moon Phase: {astronomy.moonPhase}</p>
        </div>
      )}

      {/* Time Zone */}
      {timeZone && (
        <div className="time-zone">
          <h3>Time Zone</h3>
          <p>Current Time: {timeZone.currentTime}</p>
          <p>Timezone: {timeZone.timezone}</p>
        </div>
      )}

      {/* Forecast Graph (Example using Chart.js or similar library) */}
      {forecastGraph && (
        <div className="forecast-graph">
          {/* Add Chart.js or any other library to display forecast data */}
        </div>
      )}
    </div>
  );
};

export default HomePage;
