import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';  // Updated imports
import WeatherDetails from '../components/WeatherDetails';  // Corrected path for WeatherDetails component
import './App.css';  // This should work if App.css is in the same folder as App.jsx


const App = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState(null);

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (searchTerm.trim() !== '') {
      try {
        console.log("Fetching weather data for:", searchTerm);
        const response = await fetch(`http://localhost:5000/api/weather/current?q=${searchTerm}`);
  
        // Check if the response is ok
        if (!response.ok) {
          console.error('Response failed:', response.status, response.statusText);
          setError('Failed to fetch weather data');
          return;
        }
  
        const data = await response.json();
        console.log('Weather data:', data);
        setWeatherData(data);
        setError(null);  // Reset error if request was successful
      } catch (err) {
        console.error('Error during fetch:', err);
        setError('Error fetching weather data');
        setWeatherData(null);
      }
    }
  };
  

  return (
    <Router>
      <div>
        <nav>
          <Link to="/">Home</Link>
          <Link to="/details">Weather Details</Link>
          <Link to="/settings">Settings</Link>
          <Link to="/about">About Us</Link>
        </nav>

        <Routes>
          <Route path="/" element={
            <div>
              <form onSubmit={handleSubmit}>
                <input 
                  type="text" 
                  value={searchTerm} 
                  onChange={handleSearch} 
                  placeholder="Enter location"
                />
                <button type="submit">Search</button>
              </form>

              {error && <p>{error}</p>}
              {weatherData && (
                <div>
                  <h2>Weather Information</h2>
                  <p>Temperature: {weatherData.current.temp_c}Â°C</p>
                  <p>Humidity: {weatherData.current.humidity}%</p>

                  {/* Render clickable weather details */}
                  <div>
                    <h3>Details</h3>
                    <Link to={`/details/uv`}>UV Index: {weatherData.current.uv}</Link><br />
                    <Link to={`/details/wind`}>Wind Speed: {weatherData.current.wind_kph} km/h</Link><br />
                    <Link to={`/details/humidity`}>Humidity: {weatherData.current.humidity}%</Link><br />
                  </div>
                </div>
              )}
            </div>
          } />

          <Route path="/details/:weatherAspect" element={<WeatherDetails />} />
          {/* Add routes for other pages like Settings */}
          {/* Example: */}
          {/* <Route path="/settings" element={<Settings />} /> */}
        </Routes>
      </div>
    </Router>
  );
};

export default App;
