// HomePage.jsx

import React, { useState, useEffect } from 'react'; // React import
import { fetchWeather } from '../api/fetchWeather';
import { fetchAirQuality } from '../api/fetchAirQuality';
import { fetchForecast } from '../api/fetchForecast';

const HomePage = () => {
  const [location, setLocation] = useState('New York');
  const [weather, setWeather] = useState(null);

  useEffect(() => {
    fetchWeather(location).then(data => setWeather(data));
  }, [location]);

  return (
    <div className="container">
      <h1>Weather App</h1>
      <div>
        <input
          type="text"
          placeholder="Enter location"
          value={location}
          onChange={e => setLocation(e.target.value)}
        />
        <button onClick={() => fetchWeather(location).then(data => setWeather(data))}>
          Search
        </button>
      </div>
      {weather && (
        <div>
          <h2>{weather.location.name}</h2>
          <p>Temperature: {weather.current.temp_c} Â°C</p>
          <p>Condition: {weather.current.condition.text}</p>
        </div>
      )}
    </div>
  );
};

export default HomePage;  // Ensure only one export default statement
