import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';  
import WeatherDetails from './components/WeatherDetails';  
import './App.css';

const App = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState(null);

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (searchTerm.trim() !== '') {
      try {
        // Ensure your backend server is running at this URL
        const response = await fetch(`http://localhost:5000/api/weather/current?q=${searchTerm}`);

        // If the response status is not OK, throw an error
        if (!response.ok) {
          throw new Error(`Error: ${response.statusText}`);
        }

        const data = await response.json();
        setWeatherData(data);
        setError(null);  // Reset any previous error
      } catch (err) {
        console.error("Error fetching data:", err);  // Log the error to the console
        setError('Error fetching weather data');
        setWeatherData(null);
      }
    } else {
      setError('Please enter a valid location');
    }
  };

  return (
    <Router>
      <div>
        <nav>
          <Link to="/">Home</Link>
          <Link to="/details">Weather Details</Link>
          <Link to="/settings">Settings</Link>
        </nav>

        <Switch>
          <Route path="/" exact>
            <form onSubmit={handleSubmit}>
              <input 
                type="text" 
                value={searchTerm} 
                onChange={handleSearch} 
                placeholder="Enter location"
              />
              <button type="submit">Search</button>
            </form>

            {error && <p>{error}</p>}
            {weatherData && (
              <div>
                <h2>Weather Information</h2>
                <p>Temperature: {weatherData.current.temp_c}Â°C</p>
                <p>Humidity: {weatherData.current.humidity}%</p>

                <div>
                  <h3>Details</h3>
                  <Link to="/details/uv">UV Index: {weatherData.current.uv}</Link><br />
                  <Link to="/details/wind">Wind Speed: {weatherData.current.wind_kph} km/h</Link><br />
                  <Link to="/details/humidity">Humidity: {weatherData.current.humidity}%</Link><br />
                </div>
              </div>
            )}
          </Route>

          <Route path="/details/:weatherAspect" component={WeatherDetails} />
        </Switch>
      </div>
    </Router>
  );
};

export default App;
