const App = () => {
    const [searchTerm, setSearchTerm] = useState('');  // Store search term
    const [weatherData, setWeatherData] = useState(null);  // Weather data state
    const [error, setError] = useState(null);  // Error state
  
    const handleSearch = (event) => {
      setSearchTerm(event.target.value);  // Update search term as user types
    };
  
    const handleSubmit = async (event) => {
      event.preventDefault();  // Prevent form default behavior (page reload)
      
      if (searchTerm.trim() !== '') {
        try {
          const response = await fetch(`http://localhost:5000/api/weather/current?q=${searchTerm}`);
          const data = await response.json();
          setWeatherData(data);  // Set the fetched data
          setError(null);  // Clear any previous errors
        } catch (err) {
          setError('Error fetching weather data');
          setWeatherData(null);  // Reset weather data in case of error
        }
      }
    };
  
    return (
      <Router>
        <nav>
          <ul>
            <li><Link to="/">Home</Link></li>
            <li><Link to="/details">Weather Details</Link></li>
            <li><Link to="/settings">Settings</Link></li>
            <li><Link to="/about">About Us</Link></li>
            <li><Link to="/feedback">Feedback</Link></li>
          </ul>
        </nav>
  
        <div>
          <form onSubmit={handleSubmit}>
            <input 
              type="text" 
              value={searchTerm} 
              onChange={handleSearch} 
              placeholder="Enter location"
            />
            <button type="submit">Search</button>
          </form>
        </div>
  
        {/* Conditional rendering: Display weather data only if available */}
        {error && <p>{error}</p>}
        {weatherData ? (
          <div>
            <h2>Weather Information</h2>
            <p>Location: {weatherData.name}</p>  {/* Access 'name' safely */}
            <p>Temperature: {weatherData.main.temp}Â°C</p>
            <p>Humidity: {weatherData.main.humidity}%</p>
          </div>
        ) : (
          <p>No weather data available.</p> /* Message when data isn't available */
        )}
  
        {/* Routes for different pages */}
        <Routes />
      </Router>
    );
  };
