import React, { useState } from 'react';
import { BrowserRouter as Router, Link } from 'react-router-dom';  // Import Link for navigation
import Routes from './Routes';  // Routes component that handles different routes

const App = () => {
  const [searchTerm, setSearchTerm] = useState('');  // Store search term

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);  // Update search term as user types
  };

  const handleSubmit = async (event) => {
    event.preventDefault();  // Prevent form default behavior (page reload)
    
    // Fetch weather data if search term is valid
    if (searchTerm.trim() !== '') {
      try {
        const response = await fetch(`http://localhost:5000/api/weather/current?q=${searchTerm}`);
        const data = await response.json();
        console.log(data);  // Log data to the console (you can update state to display the data)
      } catch (err) {
        console.error("Error fetching weather data", err);
      }
    }
  };

  return (
    <Router>
      {/* Navigation Bar */}
      <nav>
        <ul>
          <li><Link to="/">Home</Link></li>
          <li><Link to="/details">Weather Details</Link></li>
          <li><Link to="/settings">Settings</Link></li>
          <li><Link to="/about">About Us</Link></li>
          <li><Link to="/feedback">Feedback</Link></li>
        </ul>
      </nav>

      {/* Search Form */}
      <div>
        <form onSubmit={handleSubmit}>  {/* Form submission to trigger search */}
          <input 
            type="text" 
            value={searchTerm} 
            onChange={handleSearch} 
            placeholder="Enter location"
          />
          <button type="submit">Search</button>  {/* Search button */}
        </form>
      </div>

      {/* Routes for different pages */}
      <Routes />
    </Router>
  );
};

export default App;
