import React from 'react';
import { useParams } from 'react-router-dom';

const WeatherDetails = () => {
  const { weatherAspect } = useParams();  // Get the aspect from the URL
  let detailContent;

  // Fetch or display information based on the weather aspect
  switch (weatherAspect) {
    case 'uv':
      detailContent = (
        <div>
          <h2>UV Index</h2>
          <p>The UV index measures the risk of harm from unprotected sun exposure. A higher UV index means greater risk.</p>
        </div>
      );
      break;
    case 'wind':
      detailContent = (
        <div>
          <h2>Wind Speed</h2>
          <p>Wind speed is a measure of how fast the air is moving. Higher wind speeds can cause storms and influence weather patterns.</p>
        </div>
      );
      break;
    case 'humidity':
      detailContent = (
        <div>
          <h2>Humidity</h2>
          <p>Humidity is the amount of moisture in the air. High humidity can make it feel hotter, while low humidity can cause dry skin.</p>
        </div>
      );
      break;
    default:
      detailContent = <p>Details not available.</p>;
  }

  return (
    <div>
      {detailContent}
    </div>
  );
};

export default WeatherDetails;
