import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';  // Import necessary components
import WeatherDetails from './components/WeatherDetails';  // New component for detailed weather
import './App.css';  // Import the stylesheet if necessary

const App = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState(null);

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (searchTerm.trim() !== '') {
      try {
        const response = await fetch(`http://localhost:5000/api/weather/current?q=${searchTerm}`);
        const data = await response.json();
        setWeatherData(data);
        setError(null);  // Reset any previous error
      } catch (err) {
        setError('Error fetching weather data');
        setWeatherData(null);
      }
    }
  };

  return (
    <Router>
      <div>
        <nav>
          <Link to="/">Home</Link>
          <Link to="/details">Weather Details</Link>
          <Link to="/settings">Settings</Link>
          <Link to="/about">About Us</Link>
        </nav>

        <Switch>
          <Route path="/" exact>
            {/* Home Page */}
            <form onSubmit={handleSubmit}>
              <input 
                type="text" 
                value={searchTerm} 
                onChange={handleSearch} 
                placeholder="Enter location"
              />
              <button type="submit">Search</button>
            </form>

            {error && <p>{error}</p>}
            {weatherData && (
              <div>
                <h2>Weather Information</h2>
                <p>Temperature: {weatherData.current.temp_c}°C</p>
                <p>Humidity: {weatherData.current.humidity}%</p>

                {/* Render clickable weather details */}
                <div>
                  <h3>Details</h3>
                  <Link to={`/details/uv`}>UV Index: {weatherData.current.uv}</Link><br />
                  <Link to={`/details/wind`}>Wind Speed: {weatherData.current.wind_kph} km/h</Link><br />
                  <Link to={`/details/humidity`}>Humidity: {weatherData.current.humidity}%</Link><br />
                </div>
              </div>
            )}
          </Route>

          <Route path="/details/:weatherAspect" component={WeatherDetails} />
          {/* Add routes for other pages like Settings */}
          {/* Example: */}
          {/* <Route path="/settings" component={Settings} /> */}
        </Switch>
      </div>
    </Router>
  );
};

export default App;
