import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import WeatherDetails from '../components/WeatherDetails';  // Correct relative path
// Make sure this is the correct path
import './App.css';

const App = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState(null);

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (searchTerm.trim() !== '') {
      try {
        const response = await fetch(`http://localhost:000/api/weather/current?q=${searchTerm}`);
        
        // Check the response status
        console.log("Response Status:", response.status); // Log status
  
        if (!response.ok) {
          throw new Error(`Error: ${response.statusText}`);
        }
  
        const data = await response.json();
        console.log("Fetched Weather Data:", data);  // Log the actual data
        setWeatherData(data);
        setError(null);  // Reset any previous error
      } catch (err) {
        console.error("Error fetching data:", err);  // Log the error
        setError('Error fetching weather data');
        setWeatherData(null);
      }
    } else {
      setError('Please enter a valid location');
    }
  };
  
  return (
    <Router>
      <div>
        <nav>
          <Link to="/">Home</Link> | 
          <Link to="/details">Weather Details</Link> | 
          <Link to="/settings">Settings</Link>
        </nav>

        <Routes>
          <Route
            path="/"
            element={
              <div>
                <h1>Weather App</h1>
                <form onSubmit={handleSubmit}>
                  <input
                    type="text"
                    value={searchTerm}
                    onChange={handleSearch}
                    placeholder="Enter location"
                  />
                  <button type="submit">Search</button>
                </form>

                {error && <p>{error}</p>}

                {weatherData && (
                  <div>
                    <h2>Weather Information</h2>
                    <p>Temperature: {weatherData.current.temp_c}Â°C</p>
                    <p>Humidity: {weatherData.current.humidity}%</p>
                    <div>
                      <h3>Details</h3>
                      <Link to="/details/uv">UV Index: {weatherData.current.uv}</Link><br />
                      <Link to="/details/wind">Wind Speed: {weatherData.current.wind_kph} km/h</Link><br />
                      <Link to="/details/humidity">Humidity: {weatherData.current.humidity}%</Link><br />
                    </div>
                  </div>
                )}
              </div>
            }
          />
          <Route path="/details/:weatherAspect" element={<WeatherDetails />} />
          {/* Add routes for other pages like Settings */}
        </Routes>
      </div>
    </Router>
  );
};

export default App;
